import React, { useState } from 'react';
import Layout from '../Layout'
import PlsqlMenu from './PlsqlMenu'
const Ppretest = () => {
	const [questions, setQuestions] = useState([
	  // Your quiz questions and options here
	]);
  
	const [score, setScore] = useState(0);
  
	const handleQuestionChange = (questionIndex, selectedOption) => {
	  const updatedQuestions = [...questions];
	  updatedQuestions[questionIndex].selectedOption = selectedOption;
	  setQuestions(updatedQuestions);
	};
  
	const handleSubmit = () => {
	  let newScore = 0;
	  questions.forEach((question) => {
		if (question.selectedOption === question.correctAnswer) {
		  newScore++;
		}
	  });
	  setScore(newScore);
	};
  
  return (
	<div>
	  <Layout >
		  <div className="container-flui m-3 p-3">
			<div className="row">
			  <div className="col-md-3">
			   <PlsqlMenu/>
			  </div>
			  <div className="col-md-9">
			 
				 <h1>PostTest of plsql</h1>
				
			  </div>
			</div>
		  </div>
		</Layout>
	</div>
  )
}

export default Pposttest
